function debounce(func, wait) {
  let timeout = null;
  return (...args) => {
    const context = this;
    clearTimeout(timeout);
    timeout = setTimeout(func, wait);
    if (callNow) func.apply(context, args);
  };
}

const throttle = (func, limit) => {
  let inThrottle;
  return (...args) => {
    const context = this;
    if (!inThrottle) {
      func.apply(context, args);
      inThrottle = true;
      setTimeout(() => {
        inThrottle = false;
      }, limit);
    }
  };
};

export { debounce, throttle };
